import { createContext, useReducer } from "react";

export let foodContext = createContext(0);

const defultCartState = {
    items: [],
    totalAmount:0
}

function cartReduser(state, action)
{
    if (action.type === 'ADD')
    {
        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount
        const existingItemIndex = state.item.findIndex((item) => item.id === action.item.id)
        const existingItem = state.items[existingItemIndex]
        let updatedItem;
        let updatedItems;
        if (existingItem)
        {
            updatedItem = {
                ...existingItem,
                amount:existingItem.amount +action.item.amount
            },
                updatedItems = [...state.items]
            updatedItems[existingItemIndex]=updatedItem
        } else
        {
            updatedItem = { ...action.item }
            const updatedItems = state.items.concat(action.item)
            
        }
        
        return {
            items: updatedItems,
            totalAmount:updatedTotalAmount
        }
    }
    return defultCartState
}



export default  function FoodContextProvider(props)
{

 const [cartState, dispatchCartAction]=   useReducer(cartReduser,defultCartState)

    function addItemToCart(item)
    {
        dispatchCartAction({type:'ADD',item:item})
    }
    function removeItemFromCart(id)
    {
        dispatchCartAction({type:'REMOVE',id:id})
    }
    const cartContext = {
        items:cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemToCart,
        removeItem:removeItemFromCart
    }

    return <foodContext.Provider value={cartContext}>
        {props.children}
        </foodContext.Provider>
}
